mapper.select:
SELECT * FROM MESSAGES

	identity = #{identity}
	AND message = #{message}
	AND uploadid = #{uploadid}
	AND facebook = #{facebook}
	AND twitter = #{twitter}
	AND youtube = #{youtube}
	AND googleplus = #{googleplus}

mapper.update:
UPDATE MESSAGES SET identity	= #{identity}
					message 	= #{message},
					uploadid 	= #{uploadid},
					facebook 	= #{facebook},
					twitter    	= #{twitter},
					youtube		= #{youtube},
					googlePlus  = #{googleplus}
WHERE identity = #{identity}
